mapper.select:
SELECT tbpost.id
FROM tbpost, tbuser
WHERE tbpost.tbuser_id = tbuser.id
AND tbpost.deleted = #{deleted}
AND tbpost.process = #{process}
AND tbpost.title LIKE CONCAT('%', #{title}, '%')
AND tbpost.tbuser_id = #{tbuserId}
AND tbuser.username = #{tbuserUsername}



		AND STR_TO_DATE(#{cursor}, '%Y-%m-%d %H:%i:%s.%f') > tbpost.created_at 
		ORDER BY tbpost.created_at DESC


		AND tbpost.created_at > STR_TO_DATE(#{cursor}, '%Y-%m-%d %H:%i:%s.%f')
		ORDER BY tbpost.created_at ASC




		AND #{cursor} > tbpost.title
		ORDER BY tbpost.title DESC


		AND tbpost.title > #{cursor}
		ORDER BY tbpost.title ASC


LIMIT 0, #{perpage}

SELECT count(tbpost.id) as listsize
FROM tbpost
WHERE tbpost.id is not NULL
AND tbpost.deleted = #{deleted}
AND tbpost.process = #{process}
AND tbpost.title LIKE CONCAT('%', #{title}, '%')
